import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';

  onSubmit() {
    // Add login logic here (e.g., validate credentials, navigate to different pages)
    console.log('Username:', this.username);
    console.log('Password:', this.password);
    // You can implement authentication logic and navigate to appropriate pages
  }
}
