import { Injectable } from '@angular/core';
import { HttpClient,HttpErrorResponse, HttpHeaders } from '@angular/common/http';
import { Observable ,tap} from 'rxjs';
@Injectable({
  providedIn: 'root'
})
export class PatientService {
  private apiUrl = 'http://localhost:8080'; 
  constructor(private http: HttpClient) { }

  getDoctors(): Observable<any[]> {
    return this.http.get<any[]>(`${this.apiUrl}/getslots`);
  }

  getDoctorSlots(doctorId: string): Observable<any[]> {
    const url = `${this.apiUrl}/getdrslot/${doctorId}/slots`;
    return this.http.get<any[]>(url);
  }

  reserveSlot(slotId: string, patientId: string): Observable<any> {
    const body = { slotID: slotId, patientID: patientId };
    return this.http.post(`${this.apiUrl}/reserveslot`,body);
  }
}
