import { Component } from '@angular/core';
import {}

@Component({
  selector: 'app-patient',
  templateUrl: './patient.component.html',
  styleUrls: ['./patient.component.css']
})
export class PatientComponent {
  selectedDoctor: any;
  appointments: any[] = [
    { date: new Date('2023-11-15T10:00:00'), doctorName: 'Dr. Smith' },
    { date: new Date('2023-11-16T14:30:00'), doctorName: 'Dr. Johnson' }
  ];
  doctors: any[] = [];
  newAppointment: { date: string, time: string } = { date: '', time: '' };

  constructor(private doctorService: DoctorService) {
    this.loadDoctors();
  }

  loadDoctors() {
    this.doctorService.getDoctors().subscribe((doctors: any[]) => {
      this.doctors = doctors;
    });
  }

  loadDoctorSlots() {
    if (this.selectedDoctor) {
      this.doctorService.getDoctorSlots(this.selectedDoctor.id).subscribe((slots: any[]) => {
        // Assuming slots contain objects with 'date' and 'time' properties
        this.appointments = slots.map(slot => ({
          date: new Date(slot.date),
          time: new Date(slot.time),
          doctorName: this.selectedDoctor.name
        }));
      });
    }
  }

  cancelAppointment(appointment: any) {
    const index = this.appointments.indexOf(appointment);
    if (index !== -1) {
      this.appointments.splice(index, 1);
    }
  }

  editAppointment(appointment: any) {
    // Logic to edit the appointment
    console.log('Edit appointment:', appointment);
  }

  reserveAppointment() {
    const newDate = new Date(this.newAppointment.date);
    const newTime = new Date(this.newAppointment.time);
    if (!isNaN(newDate.getTime()) && !isNaN(newTime.getTime()) && this.selectedDoctor) {
      this.appointments.push({
        date: newDate,
        time: newTime,
        doctorName: this.selectedDoctor.name
      });
      this.newAppointment = { date: '', time: '' };
    } else {
      alert('Invalid date, time, or doctor selection. Please enter valid details.');
    }
  }
}
