import { Component } from '@angular/core';
import { SigninService } from './signin.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css']
})
export class SigninComponent {
  name: string = '';
  email: string = '';
  password: string = '';
  userType: string = '';

  constructor(private signinService: SigninService, private router: Router) {}
  onSubmit() {
    this.signinService.signUp(this.name, this.email, this.password, this.userType).subscribe(
      (response)=>{
        const id = response.id;
        if(response.userType=="doctor"){
          console.log('SignUp successful');
          this.router.navigate(['/doctor'],id);
          this.resetForm();

        }else if (response.userType=="patient"){
          console.log('SignUp successful');
          this.router.navigate(['/patient'],id);
          this.resetForm();
        }
        else{

        }
      },
      (error) => {
        console.error('SignUp failed:', error);
        this.resetForm();
      }
    );
  }
  private resetForm() {
    this.name = '';
    this.email = '';
    this.password = '';
    this.userType = '';

  }
}
<div class="signin-container">
  <h2>Sign Up</h2>
  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="name">Name:</label>
      <input type="text" id="name" [(ngModel)]="name" name="name" required
             aria-label="Enter your name">
    </div>
    <div class="form-group">
      <label for="email">Email:</label>
      <input type="email" id="email" [(ngModel)]="email" name="email" required
             aria-label="Enter your email">
    </div>
    <div class="form-group">
      <label for="password">Password:</label>
      <input type="password" id="password" [(ngModel)]="password" name="password" required>
    </div>
    <div class="form-group">
      <label>User Type:</label>
      <div>
        <label>
          <input type="radio" [(ngModel)]="userType" name="userType" value="patient" > Patient
        </label>
      </div>
      <div>
        <label>
          <input type="radio" [(ngModel)]="userType" name="userType" value="doctor"> Doctor
        </label>
      </div>
    </div>
    <button type="submit">Sign Up</button>
  </form>
</div>
