import { Component } from '@angular/core';
import { DoctorService } from '../doctor/doctor.service';

interface Doctor {
  id: number;
  name: string;
}

interface Slot {
  time: string;
}
@Component({
  selector: 'app-patient',
  templateUrl: './patient.component.html',
  styleUrls: ['./patient.component.css']
})
export class PatientComponent {
  selectedDoctor: Doctor | null = null;
  doctors: Doctor[] = [];
  appointments: any[] = [
    { date: new Date('2023-11-15T10:00:00'), time: new Date('2023-11-15T10:00:00'), doctorName: 'Dr. Smith' },
    { date: new Date('2023-11-16T14:30:00'), time: new Date('2023-11-16T14:30:00'), doctorName: 'Dr. Johnson' }
  ];
  newAppointment: { date: string, time: string, slot?: Slot } = { date: '', time: ''};
  availableSlots: Slot[] = [];

  constructor(private doctorService: DoctorService) {
    this.loadDoctors();
  }

  loadDoctors() {
    this.doctorService.getDoctors().subscribe((doctors: Doctor[]) => {
      this.doctors = doctors;
      console.log('Doctors:', this.doctors); // Log the doctors array to the console
    });
  }

  loadDoctorSlots() {
    if (this.selectedDoctor) {
      this.doctorService.getDoctorSlots(this.selectedDoctor.id.toString()).subscribe((slots: Slot[]) => {
        this.availableSlots = slots;
      });

    }
  }

   cancelAppointment(appointment: any) {
     const index = this.appointments.indexOf(appointment);
     if (index !== -1) {
       this.appointments.splice(index, 1);
     }
   }

  editAppointment(appointment: any) {
    // Logic to edit the appointment
    console.log('Edit appointment:', appointment);
  }

  reserveAppointment() {
    if (this.selectedDoctor && this.newAppointment.date && this.newAppointment.time) {
      const newAppointment: any = {
        date: this.newAppointment.date,
        time: this.newAppointment.time,
        doctorName: this.selectedDoctor.name
      };

      this.appointments.push(newAppointment);
      console.log('New Appointment:', newAppointment); // Log the new appointment to the console

      // Reset the form fields
      this.newAppointment = { date: '', time: '' };
      this.selectedDoctor = null;
    }
  }
}
