import { Component } from '@angular/core';
@Component({
  selector: 'app-doctor',
  templateUrl: './doctor.component.html',
  styleUrls: ['./doctor.component.css']
})
export class DoctorComponent {
 appointments: any[] = [
    { date: new Date('2023-11-15T10:00:00'), time: new Date('2023-11-15T10:00:00') },
    { date: new Date('2023-11-16T14:30:00'), time: new Date('2023-11-16T14:30:00') }
  ];
  showMessagesDropdown: boolean = false;
  doctorMessages: string[] = [
  'ReservationCreated',
  'ReservationUpdated',
  'ReservationCancelled'
]; 
  newAppointment: { date: string, time: string } = { date: '', time: '' };

  constructor(private messagingService: MessagingService) {}

  toggleMessagesDropdown() {
    this.showMessagesDropdown = !this.showMessagesDropdown;
    // if (this.showMessagesDropdown) {
    //     this.fetchDoctorMessages();
    // }
}
fetchDoctorMessages() {
  this.doctorMessages = ['Message 1', 'Message 2', 'Message 3'];
}
  viewMessages() {
    console.log('View Messages clicked');
}
  editAppointment(appointment: any) {
    console.log('Edit appointment:', appointment);
  }

  cancelAppointment(appointment: any) {
    const index = this.appointments.indexOf(appointment);
    if (index !== -1) {
      this.appointments.splice(index, 1);
    }
  }

  addAppointment() {
    const parsedTime = this.parseTimeString(this.newAppointment.time);

    if (parsedTime) {
      this.appointments.push({
        date: new Date(this.newAppointment.date),
        time: parsedTime
      });
      this.newAppointment = { date: '', time: '' };
    } else {
      console.error('Invalid time format');
      // Handle the error as needed
    }
  }

  private parseTimeString(timeString: string): Date | null {
    // Split the time string into hours, minutes, and AM/PM parts
    const [hoursStr, minutesStr] = timeString.split(':');
    const hours = parseInt(hoursStr, 10);
    const minutes = parseInt(minutesStr, 10);

    // Check if the provided time is valid
    if (isNaN(hours) || isNaN(minutes)) {
      console.error('Invalid time format. Please enter a valid time.');
      return null;
    }

    // Create a new Date object and set the hours and minutes
    const date = new Date();
    date.setHours(hours, minutes, 0, 0);

    return date;
  }

  private sendMessageToDoctor(appointment: any, operation: string) {
    const message = {
      doctorId: '111', // Replace with the actual doctor ID
      patientId: '222', // Replace with the actual patient ID
      operation,
      appointment
    };

    this.messagingService.sendMessage(message);
  }

}
