import { Injectable } from '@angular/core';
import { HttpClient,HttpErrorResponse, HttpHeaders } from '@angular/common/http';
import { Observable ,tap} from 'rxjs';
@Injectable({
  providedIn: 'root'
})
export class PatientService {
  private apiUrl = 'http://localhost:8080'; 
  constructor(private http: HttpClient) { }

  getSlotId(date: string, time: string, doctorName: string): Observable<any> {
    const url = `${this.apiUrl}/slotid/${date}/${time}/${doctorName}`;
    return this.http.get(url);
  }

  // Step 2: Reserve Slot
  reserveSlot(slotId: string, patientId: string): Observable<any> {
    const url = `${this.apiUrl}/reserveslot/${slotId}/${patientId}`;
    return this.http.post(url, {});
  }
}
